import React, { useEffect, useState } from "react";
import axios from "axios";
import Slider from "react-slick";

import "./MainPage.css";
import MovieCard from "../Movies/MovieCard";

const MainPage = () => {
  const api_key = "f3855f8bdde19bb1dd9b18e80bc383b6";
  const [popularMovies, setPopularMovies] = useState([]);
  const [weeklyPopularMovies, setWeeklyPopularMovies] = useState([]);
  const [curentCinemaMovies, setCurrentCinemaMovies] = useState([]);
  const [soonReleasedMovies, setSoonReleasedMovies] = useState([]);
  const [error, setError] = useState([]);

  useEffect(() => {
    axios
      .get(`https://api.themoviedb.org/3/movie/popula?api_key=${api_key}`)
      .then((response) => setPopularMovies(response.data.results))
      .catch((error) => setError(error.message));
    axios
      .get(
        `https://api.themoviedb.org/3/trending/movie/week?api_key=${api_key}`
      )
      .then((response) => setWeeklyPopularMovies(response.data.results))
      .catch((error) => setError(error.message));
    axios
      .get(`https://api.themoviedb.org/3/movie/now_playing?api_key=${api_key}`)
      .then((response) => setCurrentCinemaMovies(response.data.results))
      .catch((error) => setError(error.message));
    axios
      .get(`https://api.themoviedb.org/3/movie/upcoming?api_key=${api_key}`)
      .then((response) => setSoonReleasedMovies(response.data.results))
      .catch((error) => setError(error.message));
  }, []);

  //console.log(popularMovies);
  console.log(error);

  //settings for movies slider
  const settings = {
    dots: false,
    infinite: false,
    speed: 500,
    slidesToShow: 7,
    slidesToScroll: 1,
  };

  return (
    <div className="movies_container">
      <h2>Popular Movies</h2>
      {error && popularMovies.length === 0 ? (
        <em>{error}</em>
      ) : (
        <Slider {...settings} className="movies_slider">
          {popularMovies?.map((movie) => (
            <MovieCard movie={movie} key={movie.id} />
          ))}
        </Slider>
      )}
      <h2 className="slider_heading">Trending This Week</h2>
      {error && weeklyPopularMovies.length === 0 ? (
        <em>{error}</em>
      ) : (
        <Slider {...settings} className="movies_slider">
          {weeklyPopularMovies?.map((movie) => (
            <MovieCard movie={movie} key={movie.id} />
          ))}
        </Slider>
      )}
      <h2 className="slider_heading">In Cinemas Now</h2>

      {error && curentCinemaMovies.length === 0 ? (
        <em>{error}</em>
      ) : (
        <Slider {...settings} className="movies_slider">
          {curentCinemaMovies?.map((movie) => (
            <MovieCard movie={movie} key={movie.id} />
          ))}
        </Slider>
      )}
      <h2 className="slider_heading">Released Soon</h2>
      {error && soonReleasedMovies.length === 0 ? (
        <em>{error}</em>
      ) : (
        <Slider {...settings} className="movies_slider">
          {soonReleasedMovies?.map((movie) => (
            <MovieCard movie={movie} key={movie.id} />
          ))}
        </Slider>
      )}
    </div>
  );
};

export default MainPage;
