import React from "react";

const Review = ({ review }) => {
  return (
    <div className="review">
      <div className="review_details">
        <p>{review.author}</p>
        <p>{review.created_at}</p>
      </div>
      <p>
        {review.content.length > 100
          ? review.content.slice(0, 200) + "..."
          : review.content}
      </p>
    </div>
  );
};

export default Review;
