import React, { useEffect, useState } from "react";
import axios from "axios";
import Slider from "react-slick";

import "./MainPage.css";
import MovieCard from "../Movies/MovieCard";

const MainPage = () => {
  const api_key = "f3855f8bdde19bb1dd9b18e80bc383b6";
  const [popularMovies, setPopularMovies] = useState([]);
  const [topRatedMovies, setTopRatedMovies] = useState([]);

  useEffect(() => {
    axios
      .get(`https://api.themoviedb.org/3/movie/popular?api_key=${api_key}`)
      .then((response) => setPopularMovies(response.data.results));
    axios
      .get(`https://api.themoviedb.org/3/movie/top_rated?api_key=${api_key}`)
      .then((response) => setPopularMovies(response.data.results));
  }, []);

  console.log(popularMovies);

  //settings for movies slider
  const settings = {
    dots: false,
    infinite: false,
    speed: 500,
    slidesToShow: 7,
    slidesToScroll: 1,
  };

  return (
    <div className="movies_container">
      <h2>Popular Movies</h2>
      <div className="popular_movies_container">
        <Slider {...settings}>
          {popularMovies?.map((movie) => (
            <MovieCard movie={movie} key={movie.id} />
          ))}
        </Slider>
      </div>
    </div>
  );
};

export default MainPage;
