import React from "react";
import { Link, NavLink, useNavigate } from "react-router-dom";
import "./MovieCard.css";
import heart from "../../assets/red-heart.svg";
import star from "../../assets/star.png";

const MovieCard = ({ movie, handleLike }) => {
  const navigate = useNavigate();
  return (
    <>
      <Link to={`/movie/${movie.id}`} className="movie_link">
        <div className="movie_container">
          <div className="movie_poster_container">
            <img
              id="movie_poster"
              src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
            ></img>
            <div className="heart_icon">
              <button
                onClick={() => {
                  handleLike(movie);
                }}
              >
                <img src={heart}></img>
              </button>
            </div>
          </div>

          <div className="movie_details">
            <div className="movie_date_rate">
              <p>{movie.release_date.slice(0, 4)}</p>
              <div className="movie_rating">
                <img src={star} className="icon" />
                <p>{Math.round(movie.vote_average * 10) / 10}</p>
              </div>
            </div>

            <p className="movie_description">
              {movie?.overview
                ? movie.overview.slice(0, 100) + "..."
                : movie.overview}
            </p>
          </div>
        </div>
        <h2>
          {movie?.title.length > 20
            ? movie.title.slice(0, 9) + "..."
            : movie.title}
        </h2>
      </Link>
    </>
  );
};

export default MovieCard;
