import React, { useEffect, useState } from "react";

const useData = (url) => {
  const [data, setData] = useState(null);

  useEffect(() => {
    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        setData(data.genres);
      });
  }, []);

  return { data };
};

export default useData;

