import React from "react";
import "./Review.css";

const Review = ({ review }) => {
  return (
    <div className="review">
      <div className="review_details">
        <p>{review.author}</p>
        <p>{review.created_at.slice(0, 10)}</p>
      </div>
      <div>
        <p className="review_text">
          {review.content.length > 100
            ? review.content.slice(0, 200) + "..."
            : review.content}
        </p>
      </div>
    </div>
  );
};

export default Review;
