import React, { useEffect, useState } from "react";
import {
  useLocation,
  useNavigate,
  useSearchParams,
  useParams,
} from "react-router-dom";

import _ from "lodash";
import axios from "axios";
import "./MovieList.css";
import MovieCard from "./MovieCard";
import UseMoviesData from "../../hooks/UseMoviesData";

const api_key = "f3855f8bdde19bb1dd9b18e80bc383b6";

//

//component for rendering movie objects
const MovieList = () => {
  //manage the state of movies' categories
  const [categories, setCategories] = useState([]);
  const [currentCategory, setCurrentCategory] = useState([]);
  const [movies, setMovies] = useState([]);
  const [sortingOption, setSortingOption] = useState({
    by: "default",
    order: "asc",
  });
  const [likedMovies, setLikedMovies] = useState([]);
  //get current parametrs of URL
  const [searchParams, setSearch] = useSearchParams();
  //get current page from URL
  const [pageNum, setPage] = useState(1);
  //const page = searchParams.get("page");

  const { currentCategory: urlCategory, pageNum: urlPage } = useParams();
  const navigate = useNavigate();

  //MOVIES DATA FETCHING

  //fetch movies' categories on the 1st components render
  useEffect(() => {
    fetch(`https://api.themoviedb.org/3/genre/movie/list?api_key=${api_key}`)
      .then((response) => response.json())
      .then((data) => {
        setCategories(data.genres);
      });
  }, []);

  //fetch movies of currently selected movies' category

  //const { data } = UseMoviesData(
  //`https://api.themoviedb.org/3/discover/movie?api_key=${api_key}&with_genres=${currentCategory}`
  //);

  //get the id of current category
  const handleCategoryClick = (category) => {
    setCurrentCategory(category.id);
  };

  useEffect(() => {
    //check if there is currentCategory index or empty
    //fetch(
    //`https://api.themoviedb.org/3/discover/movie?api_key=${api_key}&with_genres=${currentCategory}&page=${page}`
    //)
    //.then((response) => response.json())
    //.then((data) => {
    //console.log(data); //data stores parameters such as page number and total pages
    //setMovies(data.results);
    //});

    //reset page parameter to 1 when category changes

    if (currentCategory !== urlCategory) {
      setPage(1);
      navigate(
        `https://api.themoviedb.org/3/discover/movie?api_key=${api_key}&with_genres=${currentCategory}&page=1`
      );
    }
    //if (currentCategory) {
    //axios
    //.get(
    // `https://api.themoviedb.org/3/discover/movie?api_key=${api_key}&with_genres=${currentCategory}&page=${pageNum}`
    //)
    //.then((response) => {
    //setMovies(response.data.results);
    //console.log(response.data);
    //});
    //}

    if (!currentCategory) {
      axios
        .get(
          `https://api.themoviedb.org/3/discover/movie?api_key=${api_key}&page=${pageNum}`
        )
        .then((response) => {
          setMovies(response.data.results);
          console.log(response.data);
        });
    }
  }, [currentCategory, pageNum]);

  //RESETING PAGE URL PARAMETERS

  useEffect(() => {
    //if (page) {
    // setPage(parseInt(page));
    //console.log("Current Page is", page);
    //} else {
    //setSearch({ page: "1" });
    //}
    console.log(urlCategory);
    setCurrentCategory(urlCategory);

    setPage(Number(urlPage) || 1);
    axios
      .get(
        `https://api.themoviedb.org/3/discover/movie?api_key=${api_key}&with_genres=${currentCategory}&page=${pageNum}`
      )
      .then((response) => {
        setMovies(response.data.results);
        console.log(response.data);
      });
  }, [urlCategory, urlPage]);

  //handle Page Change

  const handlePageChange = () => {
    //const currentParams = Object.fromEntries([...searchParams]);
    //console.log(currentParams);
    //setSearch({ ...currentParams, page: parseInt(currentParams.page) + 1 });
    setPage(pageNum + 1);
    navigate(
      `https://api.themoviedb.org/3/discover/movie?api_key=${api_key}&with_genres=${currentCategory}&page=${
        pageNum + 1
      }`
    );
  };

  //SORTING MOVIES FEATURE

  //get movie sorting option
  const handleOptionChange = (event) => {
    const { name, value } = event.target;
    setSortingOption((prev) => {
      return { ...prev, [name]: value };
    });
  };
  console.log(sortingOption);

  //sort movies by sorting option with lodash library

  useEffect(() => {
    if (sortingOption.by !== "default") {
      const sortedMovies = _.orderBy(
        movies,
        [sortingOption.by],
        [sortingOption.order]
      );
      setMovies(sortedMovies);
    }
  }, [sortingOption]);

  //SAVING FAVOURITE MOVIES FEATURE

  //get liked movie
  const handleLike = (movie) => {
    const addedMovies = [...likedMovies];
    addedMovies.push({ movie: movie });
    setLikedMovies(addedMovies); //figure out how to not add movie that is already in likedMovies
  };

  //console.log(likedMovies);

  //INFINITE SCROLLING FEATURE

  useEffect(() => {
    const handleScroll = () => {
      const { scrollTop, clientHeight, scrollHeight } =
        document.documentElement;
      if (scrollTop + clientHeight >= scrollHeight - 1) {
        console.log("Reached to Bottom");
      }
    };

    //add event listener for listening to scroll event
    window.addEventListener("scroll", handleScroll);
  }, []);

  return (
    <div className="movies_data_container">
      <div className="movie_categories">
        <ul className="categories">
          {categories.map((category, index) => (
            <li
              className={currentCategory == category.id ? "active" : ""}
              key={index}
              onClick={() => {
                handleCategoryClick(category);
              }}
            >
              {category.name}
            </li>
          ))}
        </ul>
        <div className="movie_options">
          <select
            className="option"
            name="by"
            onChange={handleOptionChange}
            value={sortingOption.by}
          >
            <option value="default">Sort By</option>
            <option value="release_date">Release Date</option>
            <option value="vote_average">Rating</option>
          </select>
          <select
            className="option"
            name="order"
            onChange={handleOptionChange}
            value={sortingOption.order}
          >
            <option value="asc">Asceding</option>
            <option value="desc">Descending</option>
          </select>
        </div>
      </div>
      <div className="movie_list">
        {movies.map((movie) => (
          <MovieCard key={movie.id} movie={movie} handleLike={handleLike} />
        ))}
      </div>
      <button onClick={() => handlePageChange(2)}>Page 3</button>
    </div>
  );
};

export default MovieList;
