import React, { useEffect, useState } from "react";
import _ from "lodash";

import "./MovieList.css";
import MovieCard from "./MovieCard";

const api_key = "f3855f8bdde19bb1dd9b18e80bc383b6";

//

//component for rendering movie objects
const MovieList = () => {
  //manage the state of movies' categories

  const [categories, setCategories] = useState([]);
  const [currentCategory, setCurrentCategory] = useState([]);
  const [movies, setMovies] = useState([]);
  const [sortingOption, setSortingOption] = useState([]);
  const [likedMovies, setLikedMovies] = useState([]);
  //fetch movies' categories on the 1st components render
  useEffect(() => {
    fetch(`https://api.themoviedb.org/3/genre/movie/list?api_key=${api_key}`)
      .then((response) => response.json())
      .then((data) => {
        setCategories(data.genres);
      });
  }, []);

  //get the id of current category
  const handleCategoryClick = (category) => {
    setCurrentCategory(category.id);
  };

  //fetch movies of currently selected movies' category

  useEffect(() => {
    //check if there is currentCategory index or empty

    fetch(
      `https://api.themoviedb.org/3/discover/movie?api_key=${api_key}&with_genres=${currentCategory}`
    )
      .then((response) => response.json())
      .then((data) => setMovies(data.results));
  }, [currentCategory]);

  //get movie sorting option
  const handleOptionChange = (event) => {
    setSortingOption(event.target.value);
  };

  //CODE FOR SORTING MOVIES

  useEffect(() => {
    if (sortingOption != "default") {
      _.orderBy(movies);
    }
  }, [sortingOption]);

  //get liked movie

  const handleLike = (movie) => {
    const addedMovies = [...likedMovies];
    addedMovies.push({ movie: movie });
    setLikedMovies(addedMovies); //figure out how to not add movie that is already in likedMovies
  };

  console.log(likedMovies);
  return (
    <div className="movies_data_container">
      <div className="movie_categories">
        <ul className="categories">
          {categories.map((category, index) => (
            <li
              className={currentCategory == category.id ? "active" : ""}
              key={index}
              onClick={() => {
                handleCategoryClick(category);
              }}
            >
              {category.name}
            </li>
          ))}
        </ul>
        <div className="movie_options">
          <select
            className="option"
            onChange={handleOptionChange}
            value={sortingOption}
          >
            <option name="default">sort by release year</option>
            <option name="year_asc">oldest to newest</option>
            <option name="year_desc">newest to oldest</option>
          </select>
          <select
            className="option"
            onChange={handleOptionChange}
            value={sortingOption}
          >
            <option name="default">sort by rating</option>
            <option name="rate_asc">lowest to highest</option>
            <option name="rate_desc">highest to lowest</option>
          </select>
        </div>
      </div>
      <div className="movie_list">
        {movies?.map((movie) => (
          <MovieCard key={movie.id} movie={movie} handleLike={handleLike} />
        ))}
      </div>
    </div>
  );
};

export default MovieList;
